mapper.select:
SELECT email, name, phone, job

FROM TB_USER

WHERE email = #{email}

mapper.update:
UPDATE TB_USER

SET phone = #{phone}

WHERE email = #{email}

UPDATE TB_USER

SET pw = #{pw}

WHERE email = #{email}

